// function ExpenseItem(date, title, amount) {} OR
// function ExpenseItem(data) {}
function ExpenseItem(props) {
    //always we should have one root element()


    //static data
    // return (
    //     <div className='expense-item'>
    //         <div>Nov 4th 2022</div>
    //         <div>
    //             <h2>
    //                 LG TV
    //             </h2>
    //         </div>
    //         <div>
    //             $21.22
    //         </div>

    //     </div>
    // )



    //dynamic data

    // const date = new Date(2022, 10, 4); //month starts from 0
    // const item = "LG TV";
