    // for good dates 
    // const month = props.date.toLocaleString('en-Us', {month:'long'});
    // const day = props.date.toLocaleString('en-Us', {day:'2-digit'});
    // const year = props.date.getFullYear();

    //useState to save the data when events occurs
    useState(props.title)

    return (
        
            <div className='expense-item'>
                {/* <div>{props.date.toISOString()}</div>*/}
                {/* date we should use toISOString() method else it will not work */}

                {/* OR for understanging the dates use toLocaleString() method */}
{/*                
                <div>
                {month} / {day} / {year}
                </div>  */}

                {/* OR use nested components  */}
                <ExpenseDate date = {props.date}/>
                <div>
                        {/* use the name for which the value is assigned used in App.js while calling function  */}
                        {props.title}  
                    
                </div>
                <div>
                    ${props.amount}
                </div>
                <div >
                    Change Title
                </div>
    
            </div>
           
        )
}

export default ExpenseItem;
