import React, { useState } from "react";

const ExpenseForm = () => {

    //multiple states method1
const [enteredTitle, setEnteredTitle] = useState('');
const [enteredAmount, setEnteredAmount] = useState('');
const [enteredDate, setEnteredDate] = useState('');

  //multiple states method1
//   const [userInput, setUserInput] = useState({
//     enteredTitle :'',
//     enteredAmount : '',
//     enteredDate : ''
//   });

    const titleChangeHandler = (event) =>{
        setEnteredTitle(event.target.value)

        // method2 not recommended bcz, we can get incorrect or outdated snapshots by this
        // setUserInput({
        //     ...userInput,
        //     enteredTitle : (event.target.value)
    
        // })

        // insted method2 use fuctions
        // setUserInput((prevState) => {
        //     return{
        //     ...prevState,
        //     enteredTitle : (event.target.value)
        //     };
        // })


    }

    const amoutChangeHandler = (event) =>{
        setEnteredAmount(event.target.value)

        // method2
        // setUserInput({
        //     ...userInput, // we should not lose the other values bcz, it override the value with new one for all keys
        //     enteredAmount : (event.target.value)
    
        // })
    }
    const dateChangeHandler = (event) =>{
        setEnteredDate(event.target.value)

        // method2
        // setUserInput({
        //     ...userInput,
        //     enteredDate : (event.target.value)
    
        // })
    }

        const submitHandler = (event) =>{
            event.preventDefault();

            // create new var for the inputs and assign them the value
           const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date:new Date(enteredDate)
           };
           console.log(expenseData);

           // to set inputs t= ""

           setEnteredTitle('');

        }



    return (<form onSubmit={submitHandler}>
        <div>
            <div className="expense-controls">
                <div className="expense-control">
                    <label>Title</label>
                    {/* two way binding to change the input value to " " onFormsubmit(value = {}) */}
                    <input type='text' value={enteredTitle} onChange={titleChangeHandler}/>
                </div>
                <div className="expense-control">
                    <label>Amout</label>
                    <input type='number' value={enteredAmount} onChange={amoutChangeHandler} min='0.01' step='0.01' />
                </div>
                <div className="expense-control">
                    <label>Date</label>
                    <input type='date' value={enteredDate} onChange={dateChangeHandler} min='2019-01-01' max='2022-12-31' />
                </div>
                <div className="expense-actions">
                  <button type="submit">
                      Add Expense
                  </button>
                </div>
            </div>
        </div>
    </form>
    )

}

export default ExpenseForm;
