import Header from '../Components/Header/Header';
import React, {useState, useEffect} from 'react';
// import {Routes, Route} from 'react-router-dom';

const MenCat = ()=>{

    const [ClothList, setCloth] = useState([]);

    const baseUrl = "https://fakestoreapi.com/products/category/men's%20clothing";
  
    useEffect(()=>{
      // fetch('https://fakestoreapi.com/products')
      //         .then(res=>res.json())
      //         .then(json=>setCartList(json));}
              
  
      axios.get(baseUrl).then((response) =>{
        setCloth(response.data)
      })}
              ,[]);
  console.log(ClothList);
    return (
      <div>
        <Header></Header>
      <Menu></Menu>
      <div className='body'>
      <Cart cartItems={Cloth}></Cart>
      </div>
      <Footer></Footer>
      </div>
   );
  }
  export default MenCat;
