    useEffect(()=>{
      // fetch('https://fakestoreapi.com/products')
      //         .then(res=>res.json())
      //         .then(json=>setCartList(json));}
              
  
      axios.get(baseUrl).then((response) =>{
        setEleList(response.data)
      })}
              ,[]);
  // console.log(ELeList);
    return (
      <div>
     
  
      <Cart cartItems={ELeList}></Cart>

      
      </div>
   );

}
