import React, { useState } from "react";

const ExpenseForm = () => {

    //multiple states method1
// const [enteredTitle, setEnteredTitle] = useState('');
// const [enteredAmount, setEnteredAmount] = useState('');
// const [enteredDate, setEnteredDate] = useState('');

  //multiple states method1
  const [userInput, setUserInput] = useState({
    enteredTitle :'',
    enteredAmount : '',
    enteredDate : ''
  });

    const titleChangeHandler = (event) =>{
        // setEnteredTitle(event.target.value)

        // method2 not recommended
        setUserInput({
            ...userInput,
            enteredTitle : (event.target.value)
    
        })
    }

    const amoutChangeHandler = (event) =>{
        // setEnteredAmount(event.target.value)

        // method2
        setUserInput({
            ...userInput, // we should not lose the other values bcz, it override the value with new one for all keys
            enteredAmount : (event.target.value)
    
        })
    }
    const dateChangeHandler = (event) =>{
        // setEnteredDate(event.target.value)

        // method2
        setUserInput({
            ...userInput,
            enteredDate : (event.target.value)
    
        })
    }




    return (<form>
        <div>
            <div className="expense-controls">
                <div className="expense-control">
                    <label>Title</label>
                    <input type='text' onChange={titleChangeHandler}/>
                </div>
                <div className="expense-control">
                    <label>Amout</label>
                    <input type='number' onChange={amoutChangeHandler} min='0.01' step='0.01' />
                </div>
                <div className="expense-control">
                    <label>Date</label>
                    <input type='date' onChange={dateChangeHandler} min='2019-01-01' max='2022-12-31' />
                </div>
                <div className="expense-actions">
                  <button type="submit">
                      Add Expense
                  </button>
                </div>
            </div>
        </div>
    </form>
    )

}

export default ExpenseForm;
