import './App.css';
import Card from './components/Card';
import ExpenseItem from "./components/ExpenseItem";

function App() {
  // props the array
  const expenses = [
    {
      id: "e1",
      date: new Date(2022, 10, 4),
      itemName: "LG TV",
      amount: 1222.2
    },
    {
      id: "e2",
      date: new Date(2022, 11, 4),
      itemName: "Car",
      amount: 100569.87
    },
    {
      id: "e3",
      date: new Date(2022, 10, 10),
      itemName: "Honda",
      amount: 60291.120
    },
    {
      id: "e4",
      date: new Date(2023, 10, 4),
      itemName: "Phone",
      amount: 2291.120
    }
  ]
  // console.log(expenses[1].amount);

// behind the scene


  return (
    <div className="App">
      <Card className="App-header">
        {/* <ExpenseItem itemName='Pg TV'></ExpenseItem> OR */}
        <ExpenseItem date={expenses[0].date} title={expenses[0].itemName} amount={expenses[0].amount}></ExpenseItem>

        {/* reuse the components, pass data in ExpenseItem.js using some attributes(set properties using (props)) */}
        <ExpenseItem date={expenses[1].date} title={expenses[1].itemName} amount={expenses[1].amount}></ExpenseItem>
        <ExpenseItem date={expenses[2].date} title={expenses[2].itemName} amount={expenses[2].amount}></ExpenseItem>
        <ExpenseItem date={expenses[3].date} title={expenses[3].itemName} amount={expenses[3].amount}></ExpenseItem>
        {/* <ExpenseItem date= {expenses[0].date} title={expenses[0].itemName} amount= {expenses[0].amount}></ExpenseItem> */}
      </Card>
    </div>
  );
}

export default App;
