import React, { useState} from "react";
import ExpenseItem from "./ExpenseItem";
import '../UI/Card.css'
import Card from "./Card";
import ExpenseFilter from "./ExpenseFilter";

const Expense = (props) =>{

    const [filteredYear, setFilteredYear] = useState('2020')
const filterChangeHandler = selectYear =>{
  setFilteredYear(selectYear)
}
  

    return(
        <Card className="app-header">

<ExpenseFilter selected={filteredYear} onChangeFilter={filterChangeHandler}/>
        
        {/* <ExpenseItem date={props.items[0].date} title={props.items[0].itemName} amount={props.items[0].amount}></ExpenseItem> */}
    {/* reuse the components, pass data in ExpenseItem.js using some attributes(set properties using (props))  */}
        {/* <ExpenseItem date={props.items[1].date} title={props.items[1].itemName} amount={props.items[1].amount}></ExpenseItem>
        <ExpenseItem date={props.items[2].date} title={props.items[2].itemName} amount={props.items[2].amount}></ExpenseItem>
        <ExpenseItem date={props.items[3].date} title={props.items[3].itemName} amount={props.items[3].amount}></ExpenseItem> */} 
        
        
        {props.items.map(expense => (<ExpenseItem title= {expense.itemName}></ExpenseItem>))}
        </Card>
    );
}
export default Expense;
