import '../UI/Cart.css'
import { Link } from 'react-router-dom';
import { Button } from 'react-bootstrap';
import axios from 'axios';
const Cart = (props)=>{


  const onDelete = ()=>{
    axios.delete('https://fakestoreapi.com/products/6')
  }
    
// console.log(props.cartItems);
    return(
        <div className="cart-container">
            {props.cartItems.map(item => (<div className='carts' key={item.id}>
                <img alt="img..." src={item.image}></img>
                <p>{item.title}</p>
                <span>$ {item.price} </span>
                <span>50% off</span>
                <p>Rating: {item.rating.rate}</p>
                {/* <Link to={`/products/${item.id}`}>view details</Link> */}
                <Link
                to={`/product-details/${item.id}`}
                className="product-card-button"
              >
                View Details
              </Link>
              <br />
              <Button onClick={(item.id).onDelete}>Delete Product</Button>
            </div>))}
        </div>
    )

}
export default Cart;
