import CartDetails from './CartDetails';
import '../UI/Cart.css'
const Cart = (props)=>{
    

    return(
        <div className="cart-container">
            {props.cartItems.map(item => (<div className='carts' id={item.id}>
                <img src={item.image} onClick={() => <CartDetails></CartDetails>}></img>
                <p>{item.title}</p>
                <span>$ {item.price} </span>
                <span>50% off</span>
            </div>))}
        </div>
    )

}
export default Cart;
