import '../Components/UI/AddProducts.css';
import {Form, Button } from 'react-bootstrap';
import React, { useState } from "react";
import axios from "axios";

function AddProducts() {
   const [text, setText] = useState();
  const [data, setData] = useState({
    title: "",
    description: "",
    price: "",
    image: null,
  });
  
const formValidation = (items)=>{
    for(let el of items){
        console.log(el);
    }

}


  const submitProduct = (e) => {
    e.preventDefault();
    axios({
      method: "POST",
      baseURL: "https://fakestoreapi.com",
      url: `/products`,
      data: data,
    }).then((response) => {
    //   console.log("response:", response.data);
     
      formValidation(response.data);


    });




    
    setData({
        title: "",
        description: "",
        price: "",
        image: null,
      });

  };

  return (
    <div className="App">
      <h1>Add a new Product</h1>
      <Form className="d-flex justify-content-center flex-column">
        <div className="d-flex flex-column">
          <label>Title</label>
          <input className="form"
            type="text"
            value={data.title}
            onChange={(e) => {
              setData({ ...data, title: e.target.value });  
            }}
          />
        </div>
        <div className="d-flex flex-column">
          <label>Description</label>
          <textarea className="form"
            value={data.description}
            onChange={(e) => {
              setData({ ...data, description: e.target.value });
            }}
          />
        </div>{" "}
        <div className="d-flex flex-column">
          <label>Price</label>
          <input className="form"
            type="number"
            value={data.price}
            onChange={(e) => {
              setData({ ...data, price: e.target.value });
            }}
          />
        </div>
        <div className="d-flex flex-column">
          <label>Image</label>
          <input className="form"
            type={"file"}
            onChange={(e) => {
              setData({
                ...data,
                image: URL.createObjectURL(e.target.files[0]),
              });
            }}
          />{data.image &&
          <img className="img" alt='img...'
            src={data.image}
          />}
        </div>
    
        <Button className="form" onClick={submitProduct} as="a" variant="primary">
        Submit Product
  </Button>
      </Form>
      <p className='required'>{text}</p>
    </div>
  );
}

export default AddProducts;
